import { useContext, useState } from 'react';
import styles from './Cart.module.css';
import Modal from '../UI/Modal';
import CartContext from '../../store/cart-context';
import CartItem from './CartItem';
import CheckOut from './CheckOut';

const Cart = (props) => {
  const [isCheckout, setIsCheckout] = useState(false);
  const cartCtx = useContext(CartContext);
  const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;
  const hasItems = cartCtx.items.length > 0;
  const cartItemAddHandler = (item) => {
    cartCtx.addItem({ ...item, amount: 1 });
  };
  const cartItemRemoveHandler = (id) => {
    cartCtx.removeItem(id);
  };
  const orderHandler = () => {
    setIsCheckout(true);
  };
  const cartItems = (
    <ul className={styles['cart-items']}>
      {cartCtx.items.map((item) => (
        <CartItem
          key={item.id}
          amount={item.amount}
          name={item.name}
          price={item.price}
          onRemove={cartItemRemoveHandler.bind(null, item.id)}
          onAdd={cartItemAddHandler.bind(null, item)}
        />
      ))}
    </ul>
  );
  const closeHandler = () => {
    setIsCheckout(false);
  };
  const modalActions = (
    <div className={styles.actions}>
      <button onClick={props.onClose} className={styles['button--alt']}>
        Close
      </button>
      {hasItems && (
        <button onClick={orderHandler} className={styles.button}>
          Order
        </button>
      )}
    </div>
  );
  const submitOrderHandler = (userData) => {
    fetch('https://react-http-960ff-default-rtdb.firebaseio.com/orders.json', {
      method: 'POST',
      body: JSON.stringify({ userData: userData, orderedItems: cartCtx.items }),
      headers: {
        'Content-Type': 'application/json',
      },
    });
  };
  return (
    <Modal onClose={props.onClose}>
      {cartItems}
      {!isCheckout && modalActions}
      <div className={styles.total}>
        <span>Total amount</span>
        <span>{totalAmount}</span>
      </div>
      {isCheckout && (
        <CheckOut onSubmit={submitOrderHandler} onClose={closeHandler} />
      )}
    </Modal>
  );
};
export default Cart;
